def IncreasingSequences(V):
	n = list(range(0,len(V)))
	L = [1] * len(V)

	for i in range(1, len(V)):
		for j in range(1, i+1):
			if V[i-j] < V[i] and L[i] <= L[i-j]:
				L[i] = L[i-j] + 1

	longest_length = 0
	id_of_longest = 0

	for i in range(len(V)):
		if L[i] > longest_length:
			longest_length = L[i]
			id_of_longest = i

	sequence = [V[id_of_longest]]
	length_sequence = [longest_length]

	for i in range(id_of_longest-1, -1, -1):
		if V[i] < sequence[0] and L[i] == length_sequence[0] - 1:
			sequence.insert(0, V[i])
			length_sequence.insert(0, L[i])

	return sequence, longest_length


def MakeChange(C, N):
	num_coins = [0] * int(N+1)

	for n in range(1, N+1):
		lowest_num_coins_list = []
		for c in C:
			if n - c >= 0:
				lowest_num_coins_list.append(num_coins[n - c] + 1)

		num_coins[n] = min(lowest_num_coins_list)

	coins = []
	cent_values = [N]
	for i in range(N, -1, -1):
		if num_coins[i] == num_coins[cent_values[0]] - 1 and cent_values[0] - i in C:
			coins.append(cent_values[0] - i)
			cent_values.insert(0, i)

	return coins


def LongestPalindrome(my_string):
	S = [*my_string]
	L = [1] * len(S)
	palindrome = ""

	previous_possible_L_list = []

	for n in range(1, len(S)):

		possible_L_list = []

		if S[n] == S[n-1]:
			possible_L_list.append(2)

		if S[n] == S[n-2] and n-2 >= 0:
			possible_L_list.append(3)

		for j in previous_possible_L_list:
			if S[n] == S[n-j-1] and n-j-1 >= 0:
				possible_L_list.append(j + 2)


		best_L = 1
		for l in possible_L_list:
			if l > best_L:
				best_L = l
		L[n] = best_L

		previous_possible_L_list = possible_L_list

	longest = 0
	longest_index = 0

	for index, l in enumerate(L):
		if l > longest:
			longest = l
			longest_index = index

	for s in S[longest_index-longest+1:longest_index+1]:
		palindrome += s

	return palindrome, longest


def RoadTrip(locations):
	locations.insert(0, 0)
	best_penalty_list = [0]

	for n in range(1, len(locations)):
		local_penalty_list = []

		for h in range(0, n):
			local_penalty = (200 - abs(locations[n] - locations[h]))**2
			local_penalty += best_penalty_list[h]
			local_penalty_list.append(local_penalty)

		best_penalty = min(local_penalty_list)
		best_penalty_list.append(best_penalty)			

	list_of_stops = [len(locations) - 1]
	final_penalty = best_penalty_list[len(locations)-1]

	for i in range(list_of_stops[0] - 1, -1, -1):
		possible_penalty = (200 - abs(locations[list_of_stops[0]] - locations[i]))**2
		possible_final_penalty = possible_penalty + best_penalty_list[i]
		if possible_final_penalty == best_penalty_list[list_of_stops[0]]:
			list_of_stops.insert(0, i)

	print(list_of_stops)

	return list_of_stops, final_penalty
	


